import React, { useState } from "react";
import useStats from "../lib/useStats";
import StateBlock from "./StateBlock";

const StateStats = ({ url }) => {
  const { stats, loading, error } = useStats(url);
  const [selectedState, setSelectedState] = useState("Missouri");
  const [stateStats, setStateStats] = useState({
    confirmed: 0,
    active: 0,
    recovered: 0,
    deaths: 0,
  });

  if (loading) return <p>Loading....</p>;
  if (error) return <p>Error...</p>;

  let states = [];
  let c = 0,
    a = 0,
    r = 0,
    d = 0;
  for (let i = 0; i < stats.length; i++) {
    if (states.indexOf(stats[i].provinceState) === -1) {
      states.push(stats[i].provinceState);
    }
    if (stats[i].provinceState === selectedState) {
      c += stats[i].confirmed;
      a += stats[i].active;
      r += stats[i].recovered;
      d += stats[i].deaths;
    }
    states.sort();
  }
  setStateStats({
    confirmed: c,
    active: a,
    recovered: r,
    deaths: d,
  });

  return (
    <>
      <div
        style={{
          display: "flex",
          marginTop: "5vh",
          justifyContent: "center",
        }}
      >
        <span
          style={{
            margin: "2vh 2vw 0 0",
            fontSize: "2vh",
          }}
        >
          Select a state:
        </span>
        <select
          style={{
            width: "40vw",
            border: "1px solid black",
            background: "transparent",
            boxShadow: "2px 2px grey",
            fontSize: "20px",
          }}
          onChange={e => {
            setSelectedState(e.target.value);
          }}
        >
          {states.map(state => (
            <option value={state} key={state}>
              {state}
            </option>
          ))}
        </select>
      </div>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          marginTop: "5vh",
        }}
      >
        <span>Now viewing counties in:</span>
        <span
          style={{ fontWeight: "bold", fontSize: "3vh", marginLeft: "2vw" }}
        >
          {selectedState.toUpperCase()}
        </span>
      </div>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(2, 1fr)",
          gridGap: "2rem",
          margin: "5vh 0 5vh 0",
          padding: "1rem",
          justifyItems: "center",
          overflowY: "scroll",
        }}
      >
        {stats.map(state =>
          state.provinceState === selectedState ? (
            <StateBlock state={state} />
          ) : null,
        )}
      </div>
    </>
  );
};

export default StateStats;
