import React from "react";
import useStats from "../lib/useStats";

const StateBlock = ({ state }) => {
  console.log(state);

  return (
    <div
      style={{
        width: "40vw",
        height: "30vh",
        outline: "1px solid black",
      }}
    >
      <h2 style={{ textAlign: "center", margin: "0 0 .5rem 0" }}>
        {state.provinceState}
      </h2>
      <div
        style={{
          height: "1rem",
          borderTop: "1px solid black",
          width: "20vw",
          margin: "0 10vw 0 10vw",
        }}
      ></div>

      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <p>
          Confirmed: <span>{state.confirmed}</span>
        </p>
        <p>
          Active: <span>{state.active}</span>
        </p>
        <p>
          Recovered: <span>{state.recovered}</span>
        </p>
        <p>
          Deaths: <span>{state.deaths}</span>
        </p>
      </div>
    </div>
  );
};

export default StateBlock;
