import React from "react";
import useStats from "../lib/useStats";

const StateBlock = ({ state }) => {
  console.log(state);

  return (
    <div
      style={{
        width: "40vw",
        height: "30vh",
        outline: "1px solid black",
      }}
    >
      <h2 style={{ textAlign: "center", margin: "0 0 .5rem 0" }}>
        {state.provinceState}
      </h2>
      <div
        style={{
          height: "1rem",
          borderTop: "1px solid black",
          width: "20vw",
          margin: "0 10vw 0 10vw",
        }}
      ></div>

      <table>
        <tr>
          <td>Confirmed</td>
          <td>Recovered</td>
          <td>Active</td>
          <td>Deaths</td>
        </tr>
        <tr>
          <td>{state.confirmed}</td>
          <td>{state.recovered}</td>
          <td>{state.active}</td>
          <td>{state.deaths}</td>
        </tr>
      </table>
    </div>
  );
};

export default StateBlock;
