import React, { useState } from "react";
import useStats from "../lib/useStats";
import StateBlock from "./StateBlock";

const StateStats = ({ url }) => {
  const { stats, loading, error } = useStats(url);
  const [selectedState, setSelectedState] = useState("Missouri");
  const [stateStats, setStateStats] = useState({
    confirmed: 0,
    active: 0,
    recovered: 0,
    deaths: 0,
  });

  const getStateStats = selectedState => {
    let stateConfirmed = 0;
    let stateActive = 0;
    let stateRecovered = 0;
    let stateDeaths = 0;

    for (let i = 0; i < stats.length; i++) {
      if (stats[i].provinceState === selectedState) {
        stateConfirmed += stats[i].confirmed;
        stateActive += stats[i].active;
        stateRecovered += stats[i].recovered;
        stateDeaths += stats[i].deaths;
      }
    }

    setStateStats({
      confirmed: stateConfirmed,
      active: stateActive,
      recovered: stateRecovered,
      deaths: stateDeaths,
    });
  };

  if (loading) return <p>Loading....</p>;
  if (error) return <p>Error...</p>;

  let states = [];
  for (let i = 0; i < stats.length; i++) {
    if (states.indexOf(stats[i].provinceState) === -1) {
      states.push(stats[i].provinceState);
    }
    states.sort();
  }

  getStateStats(selectedState);

  return (
    <>
      <div
        style={{
          display: "flex",
          marginTop: "5vh",
          justifyContent: "center",
        }}
      >
        <span
          style={{
            margin: "2vh 2vw 0 0",
            fontSize: "2vh",
          }}
        >
          Select a state:
        </span>
        <select
          style={{
            width: "40vw",
            border: "1px solid black",
            background: "transparent",
            boxShadow: "2px 2px grey",
            fontSize: "20px",
          }}
          onChange={e => {
            setSelectedState(e.target.value);
          }}
        >
          {states.map(state => (
            <option value={state} key={state}>
              {state}
            </option>
          ))}
        </select>
      </div>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          marginTop: "5vh",
        }}
      >
        <span>Now viewing counties in:</span>
        <span
          style={{ fontWeight: "bold", fontSize: "3vh", marginLeft: "2vw" }}
        >
          {selectedState.toUpperCase()}
        </span>
      </div>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(2, 1fr)",
          gridGap: "2rem",
          margin: "5vh 0 5vh 0",
          padding: "1rem",
          justifyItems: "center",
          overflowY: "scroll",
        }}
      >
        {stats.map(state =>
          state.provinceState === selectedState ? (
            <StateBlock state={state} />
          ) : null,
        )}
      </div>
    </>
  );
};

export default StateStats;
