import React from "react";
import useStats from "../lib/useStats";
import { FaDirections } from "react-icons/fa";

const Stats = ({ url }) => {
  const { stats, loading, error } = useStats(url);
  console.log(stats, loading, error);

  if (loading) return <p>Loading....</p>;
  if (error) return <p>Error...</p>;

  const mortalityRate = (
    (100 / stats.confirmed.value) *
    stats.deaths.value
  ).toFixed(2);

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        marginTop: "5vh",
      }}
    >
      <div
        style={{
          textAlign: "center",
          height: "15vh",
          width: "100vw",
          display: "flex",
          justifyContent: "center",
          flexDirection: "column",
        }}
      >
        <h1 style={{ fontSize: "2rem" }}>Confirmed</h1>
        <h2>{stats.confirmed.value}</h2>
      </div>

      <div
        style={{
          textAlign: "center",
          height: "15vh",

          width: "100vw",
          display: "flex",
          justifyContent: "center",
          flexDirection: "column",
        }}
      >
        <h1>Recovered</h1>
        <h2>{stats.recovered.value}</h2>
      </div>

      <div
        style={{
          textAlign: "center",
          height: "15vh",

          width: "100vw",
          display: "flex",
          justifyContent: "center",
          flexDirection: "column",
        }}
      >
        <h1>Deaths</h1>
        <h2>{stats.deaths.value}</h2>
      </div>

      <div
        style={{
          textAlign: "center",
          height: "15vh",

          width: "100vw",
          display: "flex",
          justifyContent: "center",
          flexDirection: "column",
        }}
      >
        <h1>Mortality</h1>
        <h2>{mortalityRate}%</h2>
      </div>
    </div>
  );
};

export default Stats;
